import React, { Component } from 'react';
import './project.css'
import { Tabs, Tab, Grid, Cell, Card, CardTitle, CardText, CardActions, Button, CardMenu, IconButton} from 'react-mdl';

class Project extends Component {
    constructor(props) {
        super(props); {
            this.state = { activeTab: 0 }
        }
    }

    toggleCategories() {
        if(this.state.activeTab === 0) {
            return (
                <div className="projects-grid">
                {/* Project 1 */}
                <Card className="card-container" shadow={0} style={{width: '512px', margin: 'auto'}}>
                    <CardTitle style={{color: '#fff', height: '176px', background: 'url(https://cdn-images-1.medium.com/max/1200/1*EntHChgUyirgbZ9A3zTxkA.png) center / cover'}}>Dantrick</CardTitle>
                    <CardText>
                    Built in React as Frontend and Wordpress as Backend. 
                    Master thesis from my time in KYH Stockholm.
                    </CardText>
                    <CardActions border>
                        <a href="https://github.com/puttetham/dantrick" target="_blank"><Button colored>GitHub</Button></a>
                        <a href="https://puttetham.github.io/dantrick/" target="_blank"><Button colored>Livedemo</Button></a>
                    </CardActions>
                    <CardMenu style={{color: '#fff'}}>
                        <IconButton name="share" />
                    </CardMenu>
                </Card>

                 {/* Project 2 */}
                <Card className="card-container" shadow={0} style={{width: '512px', margin: 'auto'}}>
                    <CardTitle style={{color: '#fff', height: '176px', background: 'url(https://cdn-images-1.medium.com/max/1200/1*EntHChgUyirgbZ9A3zTxkA.png) center / cover'}}>Patrik Hogler - Portfolio</CardTitle>
                    <CardText>
                        My own portfolio site. Built in React.js with Material Design.
                    </CardText>
                    <CardActions border>
                        <a href="https://github.com/puttetham/patrikhogler" target="_blank"><Button colored>GitHub</Button></a>
                        <a href="https://puttetham.github.io/patrikhogler/" target="_blank"><Button colored>Livedemo</Button></a>
                    </CardActions>
                    <CardMenu style={{color: '#fff'}}>
                        <IconButton name="share" />
                    </CardMenu>
                </Card>
                </div>

            )
        } else if (this.state.activeTab === 1){
            return (
                <div><h1>This is HTML</h1></div>
            )
        } else if (this.state.activeTab === 2){
            return (
                <div><h1>This is Wordpress</h1></div>
            )
        }

    }

    render(){
        return(
            <div classname="category-tabs">
                <Tabs activeTab={this.state.activeTab} onChange={(tabId) => this.setState({ activeTab: tabId })} ripple>
                    <Tab>React</Tab>
                    <Tab>HTML/CSS + JS + PHP</Tab>
                    <Tab>Wordpress</Tab>
                </Tabs>

                <Grid className="grid-container">
                    <Cell col={12}>
                        <div className="content">

                          {this.toggleCategories()}
                        </div>
                    </Cell>
                </Grid>

            </div>
        )
    }
}

export default Project;
