import React, { Component } from 'react';
import './aboutme.css';
import { Grid, Cell} from 'react-mdl';
import './aboutme.css';
import PatrikContact from '../assets/contact-patrik.png'
import Education from './education';
import Experience from './experience';
import Skills from './skills';

class About extends Component {
    render(){
        return(
            <div className="landingcontainer">
                <Grid>
                <Cell className="left-about" col={6}>
                    <div style={{textAlign: 'center'}}>
                        <img src={PatrikContact} 
                             alt="Photo of Patrik Hogler"
                             style={{height: '350px', display: 'block', margin: 'auto', filter:'grayscale(100%',}}
                             />

                        <h2 style={{paddingTop: '.7em', textAlign: 'center', fontFamily:'Special Elite, sans-serif'}}>Patrik Hogler</h2>
                        <h4 style={{color: 'grey', fontFamily:'Special Elite, sans-serif'}}>Front End Developer</h4>
                        <hr className="project-linebreak"></hr>
                        <p style={{ width: '65%', fontFamily:'Special Elite, sans-serif', margin: '40px auto', textAlign: 'left'}}>Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the 
                            industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type 
                            and scrambled it to make a type specimen book.</p>
                    </div>
                </Cell>

                <Cell className="right-about" col={6}>
                    <h2>Education</h2>
                    <Education 
                        startYear={2016}
                        endYear={2018}
                        schoolName={'KYH Stockholm'}
                        schoolDescription={'Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industrys standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book.'}
                    />

                    <Education 
                        startYear={2005}
                        endYear={2008}
                        schoolName={'Huddinge Gymnasium'}
                        schoolDescription={'Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industrys standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book.'}
                    />

                    <hr style={{borderTop: '2px solid #fff', width: '80%', margin: '20px auto'}}></hr>

                    <Experience 
                        startYear={2006}
                        endYear={2008}
                        jobName={'Stadium'}
                        jobDescription={'Sales shurda'}
                    />
                     <Experience 
                        startYear={2006}
                        endYear={2008}
                        jobName={'Stadium'}
                        jobDescription={'Sales shurda'}
                    />
                     <Experience 
                        startYear={2006}
                        endYear={2008}
                        jobName={'Stadium'}
                        jobDescription={'Sales shurda'}
                    />
                     <Experience 
                        startYear={2006}
                        endYear={2008}
                        jobName={'Stadium'}
                        jobDescription={'Sales shurda'}
                    />

                    <hr style={{borderTop: '2px solid #fff', width: '80%', margin: '20px auto'}}></hr>

                    <Skills />


                </Cell>
                </Grid>
            </div>
        )
    }
}

export default About;
